INSERT INTO recipes (recipe_name) VALUES ('Black Bean Dip');
INSERT INTO presentations
    (recipe_id, detail, ingredients, instructions)
VALUES
    (1, 'narrative', '<ul><li>50 g dried black beans</li><li>1 tablespoon olive oil 1/2 small red pepper, deseeded and thinly sliced</li><li>1 small red onion, thinly sliced</li><li>1 teaspoon ground cumin</li><li>1 garlic clove, finely chopped</li><li>2 tomatoes, chopped 1 small red chilli, finely chopped</li><li>1 tablespoon red wine vinegar</li><li>125 ml passata</li><li>100 g feta cheese, crumbled</li><li>corn chips, to serve</li></ul>', '<ol><li>Soak the black beans in cold water overnight. Drain and rinse well. Put them in a large saucepan with plenty of water and bring to the boil. Cook for 25-30 minute, until soft to the bite. Drain well and set aside.</li><li>Heat the oil in a saucepan set over high heat and add the red pepper and onion. Reduce the heat to low, cover and cook for about 8 minutes. Add the cumin, garlic and chillies and cook for a further 2 minutes. Add the beans, tomatoes, vinegar and passata and bring to the boil. Reduce the heat and simmer rapidly for 10 minutes, until almost all the liquid has evaporated and the tomatoes start to look mushy.</li><li>Preheat the grill to high.</li><li>Transfer the bean mixture to a flameproof dish and sprinkle the crumbled feta over the top. Cook under the hot grill until the cheese is soft and just starting to brown. Serve hot with corn chips on the side for dipping.</li></ol>');
INSERT INTO presentations
    (recipe_id, detail, ingredients, instructions)
VALUES
    (1, 'segment', '<ul><li>50 g dried black beans</li><li>1 tablespoon olive oil</li><li>1/2 small red pepper, deseeded and thinly sliced 1 small red onion, thinly sliced</li><li>1 teaspoon ground cumin 1 garlic clove, finely chopped</li><li>2 tomatoes, chopped</li><li>1 small red chilli, finely chopped</li><li>1 tablespoon red wine vinegar</li><li>125 ml passata</li><li>100 g feta cheese, crumbled</li><li>corn chips, to serve</li></ul>', '<ol><li>Soak the black beans in cold water overnight.</li><li>Drain and rinse well.</li><li>Put them in a large saucepan with plenty of water.</li><li>Bring to the boil.</li><li>Cook for 25-30 minutes, until soft to the bite.</li><li>Drain well.</li><li>Set aside.</li><li>Heat the oil in a saucepan set over high heat.</li><li>Add the red pepper and onion.</li><li>Reduce the heat to low.  Cover and cook for about 8 minutes.</li><li>Add the cumin, garlic and chillies.</li><li>Cook for a further 2 minutes.</li><li>Add the beans, tomatoes, vinegar and passata.</li><li>Bring to the boil.</li><li>Reduce the heat.</li><li>Simmer rapidly for 10 minutes, until almost all the liquid has evaporated and the tomatoes start to look mushy.</li><li>Preheat the grill to high.</li><li>Transfer the bean mixture to a flameproof dish.</li><li>Sprinkle the crumbled feta over the top.</li><li>Cook under the hot grill until the cheese is soft and just starting to brown.</li><li>Serve hot with corn chips on the side for dipping.</li></ol>');
INSERT INTO presentations
    (recipe_id, detail, ingredients, instructions)
VALUES
    (1, 'step', '<ul><li>50 g dried black beans, or 100g canned red kidney beans</li><li>1 tablespoon olive oil</li><li>1/2 small red pepper</li><li>1 small red onion</li><li>1 teaspoon ground cumin</li><li>1 garlic clove</li><li>2 tomatoes</li><li>1 small red chilli</li><li>1 tablespoon red wine vinegar 125 ml passata</li><li>100 g feta cheese</li><li>corn chips</li></ul>', '<ol><li>Soak 50g black beans in cold water overnight OR use 100g canned red kidney beans and skip to step 7.</li><li>Drain and rinse well.</li><li>Put them in a large saucepan with plenty of water.</li><li>Bring to the boil.</li><li>Cook for 25-30 minutes, until soft to the bite.</li><li>Drain well and set aside.</li><li>Deseed 1/2 red pepper and slice thinly and place on a small dish.</li><li>Thinly slice 1 red onion. Add to the red pepper.</li><li>Finely chop 1 garlic clove and place on another small dish.</li><li>Finely chop 1 red chilli. Add to the garlic.</li><li>Add 1 teaspoon ground cumin to the chilli and garlic.</li><li>Chop 2 tomatoes.</li><li>Heat the oil in a saucepan set over high heat.</li><li>Add the red pepper and onion.</li><li>Reduce the heat to low.</li><li>Cover and cook for about 8 minutes.</li><li>Add the cumin, garlic and chillies.</li><li>Cook for a further 2 minutes.</li><li>Add the beans, tomatoes, 1 tablespoon vinegar and 125ml passata.</li><li>Bring to the boil.</li><li>Reduce the heat.</li><li>Simmer rapidly for 10 minutes, until almost all the liquid has evaporated and the tomatoes start to look mushy.</li><li>Preheat the grill to high.</li><li>Transfer the bean mixture to a flameproof dish.</li><li>Crumble 100g feta cheese.</li><li>Sprinkle the crumbled feta over the top of the bean mixture.</li><li>Cook under the hot grill until the cheese is soft and just starting to brown.</li><li>Serve hot with corn chips on the side for dipping.</li></ol>');
INSERT INTO recipes (recipe_name) VALUES ('Beef Burgers');
INSERT INTO presentations
    (recipe_id, detail, ingredients, instructions)
VALUES
    (2, 'narrative', '<ul><li>1 small onion list</li><li>1 garlic clove</li><li>1 small lemongrass stalk</li><li>200 g minced chicken</li><li>30 g fresh white breadcrumbs</li><li>1 tablespoon chopped fresh coriander</li><li>1 teaspoon finely grated lime zest</li><li>1 teaspoon fish sauce</li><li>1 teaspoon caster sugar</li><li>light flavoured oil, such as rapeseed, for brushing</li></ul>', '<ol><li>Peel and finely instructions grate the onion and peel and crush the garlic. Finely chop the white part of the lemongrass, then place with the chicken, onion, breadcrumbs, garlic, coriander, lime zest, fish sauce and sugar in a large bowl and mix well with your hands. Shape into 2 patties, cover and chill for at least 10 minutes.</li><li>Heat a barbecue or griddle pan until hot. Brush the burgers with a little oil and cook for 4 minutes on each side or until cooked through. Serve the burgers in soft rolls with lettuce, mint, coriander and chilli sauce.</li></ol>');
INSERT INTO presentations
    (recipe_id, detail, ingredients, instructions)
VALUES
    (2, 'segment', '<ul><li>1 small onion</li><li>1 garlic clove</li><li>1 small lemongrass stalk</li><li>200 g minced chicken</li><li>30 g fresh white breadcrumbs</li><li>1 tablespoon chopped fresh coriander</li><li>1 teaspoon finely grated lime zest</li><li>1 teaspoon fish sauce</li><li>1 teaspoon caster sugar</li><li>light flavoured oil, such as rapeseed, for brushing</li></ul>', '<ol><li>Peel and finely grate the onion.</li><li>Peel and crush the garlic.</li><li>Finely chop the white part of the lemongrass.</li><li>Place the chicken, onion, breadcrumbs, garlic, coriander, lime in a large bowl.</li><li>Mix well with your hands.</li><li>Shape into 2 patties.</li><li>Cover and chill for at least 10 minutes.</li><li>Heat a barbecue or griddle pan until hot.</li><li>Brush the burgers with a little oil.</li><li>Cook for 4 minutes on each side or until cooked through.</li><li>Serve the burgers in soft rolls with lettuce, mint, coriander and chilli sauce.</li></ol>');
INSERT INTO presentations
    (recipe_id, detail, ingredients, instructions)
VALUES
    (2, 'step', '<ul><li>1 small onion</li><li>1 garlic clove</li><li>1 small lemongrass stalk</li><li>200 g minced chicken</li><li>30 g fresh white breadcrumbs</li><li>1 tablespoon fresh coriander</li><li>1 lime</li><li>1 teaspoon fish sauce</li><li>1 teaspoon caster sugar</li><li>light flavoured oil, such as rapeseed, for brushing</li></ul>', '<ol><li>Peel and finely grate 1 onion and place in a large bowl.</li><li>Peel and crush 1 garlic clove, add to the bowl.</li><li>Finely chop the white part of 1 lemongrass stalk, add to the bowl.</li><li>Chop a sprig coriander leaves, add 1 tablespoon of leaves to the bowl.</li><li>Zest 1 lime. Add 1 teaspoon of the zest to the bowl.</li><li>Add the 200g chicken, 30g breadcrumbs, 1 teaspoon fish sauce and 1 teaspoon sugar to the bowl.</li><li>Mix well with your hands.</li><li>Shape into 2 burgers.</li><li>Cover and chill for at least 10 minutes.</li><li>Heat a barbecue or griddle pan until hot.</li><li>Brush the burgers with a little oil.</li><li>Cook for 4 minutes on each side or until cooked through.</li><li>Serve the burgers in soft rolls with lettuce, mint, coriander and chilli sauce.</li></ol>');
INSERT INTO recipes (recipe_name) VALUES ('Potato, bean and tomato salad with gremolata');
INSERT INTO presentations
    (recipe_id, detail, ingredients, instructions)
VALUES
    (3, 'narrative', '<ul><li>225 g small new potatoes, scrubbed, cut into bite-sized pieces if necessary</li><li>100 g runner beans, cut diagonally into slices</li><li>325 g ripe mixed tomatoes, (e.g. plum tomatoes, quartered lengthways; yellow and red cherry tomatoes, halved; beefsteak tomatoes, cut into wedges)</li><li>2 salad onions, thinly sliced</li><li>100 g young spinach leaves</li><li>1 ripe tomato, peeled and seeded</li><li>1‚ÅÑ2 tablespoon pesto</li><li>salt and freshly ground black pepper</li><li>1 small lemon</li><li>1 fat garlic clove, finely chopped</li><li>handful of flat-leaf parsley, roughly torn</li></ul>', '<ol><li>Cook the potatoes in a large pan of lightly salted boiling water for 10-12 minutes or until just tender. Add the runner beans and cook for a further 2 minutes. Then drain and rinse under cold running water. Toss the potatoes and beans with the mixed tomatoes, salad onions and spinach and season to taste. For the gremolata, use a vegetable peeler to pare thin strips of rind from the lemon. Carefully remove as much white pith as possible (this is bitter). Finely chop the lemon rind and toss with the garlic and parsley. Season and set aside. Squeeze the juice from the lemon and whizz in a food processor with the peeled tomato and pesto for 10-15 seconds. Pour over the potato mixture and toss through. Scatter over the gremolata to serve.</li></ol>');
INSERT INTO presentations
    (recipe_id, detail, ingredients, instructions)
VALUES
    (3, 'segment', '<ul><li>225 g small new potatoes, scrubbed, cut into bite-sized pieces if necessary</li><li>100 g runner beans, cut diagonally into slices</li><li>325 g ripe mixed tomatoes, (e.g. plum tomatoes, quartered lengthways; yellow and red cherry tomatoes, halved; beefsteak tomatoes, cut into wedges)</li><li>2 salad onions, thinly sliced</li><li>100 g young spinach leaves</li><li>1 ripe tomato, peeled and seeded</li><li>1‚ÅÑ2 tablespoon pesto</li><li>salt and freshly ground black pepper</li><li>1 small lemon</li><li>1 fat garlic clove, finely chopped</li><li>handful of flat-leaf parsley, roughly torn</li></ul>', '<ol><li>Cook the potatoes in a large pan of lightly salted boiling water for 10-12</li><li>minutes or until just tender.</li><li>Add the runner beans and cook for a further 2 minutes. Then drain and rinse under cold running water.</li><li>Toss the potatoes and beans with the mixed tomatoes, salad onions and spinach and season to taste.</li><li>For the gremolata, use a vegetable peeler to pare thin strips of rind from the lemon. Carefully remove as much white pith as possible (this is bitter). Finely chop the lemon rind and toss with the garlic and parsley. Season and set aside.</li><li>Squeeze the juice from the lemon and whizz in a food processor with the peeled tomato and pesto for 10-15 seconds.</li><li>Pour over the potato mixture and toss through.</li><li>Scatter over the gremolata to serve.</li></ol>');
INSERT INTO presentations
    (recipe_id, detail, ingredients, instructions)
VALUES
    (3, 'step', '<ul><li>225 g small new potatoes</li><li>100 g runner beans</li><li>325 g ripe mixed tomatoes, (e.g. plum tomatoes; yellow and red cherry tomatoes; beefsteak tomatoes)</li><li>2 salad onions</li><li>100 g young spinach leaves</li><li>1 ripe tomato</li><li>1‚ÅÑ2 tablespoon pesto</li><li>salt and freshly ground black pepper</li><li>1 small lemon</li><li>1 fat garlic clove</li><li>handful of flat-leaf parsley</li></ul>', '<ol><li>Fill a large pot with water and lightly salt.</li><li>Boil water.</li><li>Scrub and clean potatoes while the water boils.</li><li>If potatoes are large, cut into bite-sized pieces.</li><li>Add 225g of small new potatoes to boiling water and boil for approximately 10-12 minutes.</li><li>Cut runner beans diagonally into slices.</li><li>Add runner beans to boiling water and cook for a further 2 minutes.</li><li>Drain and rinse potatoes and beans under cold running water.</li><li>Quarter 325 g of ripe mixed tomatoes into slices.</li><li>Slice 2 salad onions thinly.</li><li>Remove 100 g of spinach leaves and brush clean.</li><li>Toss tomatoes, onions, spinach and boiled vegetables together.</li><li>Season with salt and pepper to taste.</li><li>For the gremolata, use a vegetable peeler to pare thin strips of rind from 1 lemon.</li><li>Carefully remove as much white pith as possible (this is bitter) from the lemon.</li><li>Chop 1 garlic clove very finely</li><li>Tear the flat leave parsley into small pieces.</li><li>Finely chop the lemon rind.</li><li>Toss lemon rind with the garlic and parsley.</li><li>Season with salt and pepper and set gremolata aside.</li><li>Squeeze the juice from the lemon.</li><li>Peel and de-seed one tomato.</li><li>Add lemon juice, tomatoes and pesto to a food processor and slice finely for 10-15 seconds.</li><li>Pour over the potato mixture and toss thoroughly.</li><li>Scatter over the gremolata to serve.</li></ol>');
INSERT INTO recipes (recipe_name) VALUES ('Quiche Maritime');
INSERT INTO presentations
    (recipe_id, detail, ingredients, instructions)
VALUES
    (4, 'narrative', '<ul><li>2 medium tomatoes sliced and marinated in French Dressing</li><li>1 pound of smoke fish fillets</li><li>1 cup milk</li><li>3 cups cooked rice</li><li>2 tablespoons butter, melted</li><li>3 eggs beaten</li><li>1 cup grated Canadian Cheddar</li></ul>', '<ol><li>Preheat the oven to 350 degrees F.  Marinate the sliced tomatoes in French Dressing so they are fully coated.  Take the smoked fish fillets and cut them into pieces 2-3 inches long.  Place all into a small baking dish and add milk.  Bake for 20 minutes or until fish separates into flakes with a fork.</li><li>Drain and reserve æ cup of liquid for later.</li><li>Grease a pie pltae and make a crust combining the rice, melted butter and 1 egg.  Turn the crust into the pie plate evenly.</li><li>Sprinkle the pie crust with half of the shredded Cheddar Cheese and then add slices of fish.  Then, sprinkle the other half of the cheese.  Combined the reserved poaching liquid with 2 eggs and pour into pie plate.  Back for 30 min.  Remove from oven and arrange the tomatoes around the edge, skin up.  Bake for 10 more minutes until set.  Garnish with chopped green onions or chives.</li></ol>');
INSERT INTO presentations
    (recipe_id, detail, ingredients, instructions)
VALUES
    (4, 'segment', '<ul><li>2 medium tomatoes sliced and marinated in French Dressing</li><li>1 pound of smoke fish fillets</li><li>1 cup milk</li><li>3 cups cooked rice</li><li>2 tablespoons butter, melted</li><li>3 eggs beaten</li><li>1 cup grated Canadian Cheddar</li></ul>', '<ol><li>Preheat oven to 350 degrees F</li><li>Marinate tomatoes in French Dressing</li><li>Cut fish into pieces 2-3 inches long and place in a shallow baking dish.</li><li>Add milk and bake for 20 minutes.</li><li>Drain to reserve æ cup of the liquid</li><li>Make a crust by combining the rice, butter and beating in one egg.</li><li>Grease a pie plate and turn crusted into the plate evenly over the bottom.  Make sure sides and rim form a pie shell.</li><li>Sprinkle shell with half of the cheese and arrange fish on top. Then sprinkle remainder of the cheese.</li><li>Combine the reserved liquid with egg and create a base for the quiche.  Pour into pie plate and bake for 30 minutes.</li><li>Remove from oven and arrange tomatoes with the skin up around the outside.  Return to oven and bake for 10 minutes.</li><li>Garnish with chives or chopped green onions.</li></ol>');
INSERT INTO presentations
    (recipe_id, detail, ingredients, instructions)
VALUES
    (4, 'step', '<ul><li>2 medium tomatoes</li><li>Ω cup French Dressing</li><li>1 pound smoke fish fillets</li><li>1 cup milk</li><li>1 Ω cups of rice</li><li>2 tablespoons of butter</li><li>3 eggs</li><li>1 block of Canadian Cheddar Cheese</li><li>Green Onions or Chives</li></ul>', '<ol><li>Slice tomatoes into 6 wedges each</li><li>Place tomato wedges in a bowl with French dressing to marinade</li><li>Cut fish into slices of 2-3 inches long</li><li>Place tomatoes in baking pan</li><li>Place fish in baking pan</li><li>Add milk to baking pan</li><li>Heat oven to 350 degrees F</li><li>Bake fish mixture for 20 min</li><li>While fish is baking, boil rice until soft.</li><li>Melt butter in a small saucepan</li><li>Remove baking dish from oven and drain into bowl retaining at least æ of the mixture.</li><li>Make rice crust. Place 3 cups of cooked rice and 2 tablespoons of melted butter in a bowl and mix.</li><li>Beat 1 egg and whip it into the rice crust mixture.</li><li>Spread rice crust mixture into a small pie plate.  Make the bottom even and ensure top of the plate is reached.</li><li>Grate Ω cup of cheese.</li><li>Sprinkle Ω cup of grated cheese onto pie crust.</li><li>Lay out fish slides in pie crust.</li><li>Grate Ω cup of cheese.</li><li>Sprinkle Ω cup of grated cheese onto pie topping.</li><li>Beat 2 eggs.</li><li>Mix 2 beaten eggs with reserved mixture from fish and tomato bake.</li><li>Pour egg mixture into pie crush.</li><li>Bake for 30 min at 30 degrees F</li><li>Remove from oven.</li><li>Place tomato wedges around the edge with skin up.</li><li>Bake for 10 more min.</li><li>Chop green onions or chives.</li><li>Place green onions or chives on top of baked pie.</li></ol>');
INSERT INTO recipes (recipe_name) VALUES ('TourtËre');
INSERT INTO presentations
    (recipe_id, detail, ingredients, instructions)
VALUES
    (5, 'narrative', '<ul><li>1 Ω pound of ground lean pork</li><li>1 small onion minced</li><li>Ω bup of boiling water</li><li>1 garlic clove, chopped</li><li>1 Ω teaspoons salt</li><li>º teaspoon celery salt</li><li>º teaspoon of black pepper</li><li>º teaspoon sage</li><li>pinch of ground cloves</li></ul>', '<ol><li>Cook over low heat in a large saucepan, stirring constantly until meat loses its red colour and about half of the liquid has evaporated.  cover and cook about 45 minutes longer.</li><li>Boil and mash potatoes and mix in with the meat and allow to cool.  Preheat oven to 450 degrees F.  Prepare psatry for 2-crust, 9 inch pie.  Roll out half and line a 9-inch pie plate. Fill with cooled meat mixture.  Roll out remainder of dough and cover pie.  Flute and seal edges.  Slash top of crust.  Bake for 10 min and reduce heat to 350 degrees F and bake for 30-40 minutes.</li></ol>');
INSERT INTO presentations
    (recipe_id, detail, ingredients, instructions)
VALUES
    (5, 'segment', '<ul><li>1 ¬Ω pound of ground lean pork</li><li>1 small onion minced</li><li>¬Ω bup of boiling water</li><li>1 garlic clove, chopped</li><li>1 ¬Ω teaspoons salt</li><li>¬º teaspoon celery salt</li><li>¬º teaspoon of black pepper</li><li>¬º teaspoon sage</li><li>pinch of ground cloves</li><li>2 ¬Ω cups of all purpose flour</li><li>¬æ tsp salt</li><li>‚Öî cup of cold unsalted butter, cubed</li><li>‚Öì cup of cold lard, cubed</li><li>‚Öì cup cold water</li></ul>', '<ol><li>Retrieve a 3-quart saucepan and combine meat, water, spices and garlic.</li><li>Cook over low heat, stiring constantly until meat is brown and half the liquid is gone.</li><li>Cover and cook for about 45 minutes.</li><li>While cooking meat, prepare potatoes.  Boil and mash.</li><li>Mix the meat and potatoes and allow them to cool.</li><li>Preheat oven to 450 degrees F</li><li>Prepare crust for 2-crust, 9-inch pie.  Whisk the flour and salt.</li><li>Blend or cut in butter and lard until it is in coarse crumbs.</li><li>Drizzle with water, tossing a while with a fork until ragged dough forms, and adding 1 tablespoon more water if necessary.</li><li>Divide dough in half and shape into disks.  Wrap and chill in fridge for 30 min.</li><li>Roll out 1 dough into a 9-inch pie crust and line a pie plate. Fill with the meat mixture.  Roll out and cover the pie with the second dough.</li><li>Bake at 450 degrees F for 10 minutes and reduce heat to 350 degrees F.  Bake for 30-40 more minutes.</li></ol>');
INSERT INTO presentations
    (recipe_id, detail, ingredients, instructions)
VALUES
    (5, 'step', '<ul><li>1 ¬Ω pounds of ground lean pork</li><li>1 small onioin</li><li>¬Ω cup of water</li><li>1 garlic clove</li><li>1 ¬Ω teaspoon of salt</li><li>¬º teaspoon celery salt</li><li>¬º teaspoon black pepper</li><li>¬º teaspoon sage</li><li>1 knife-tip of ground cloves</li><li>3 potatoes</li><li>2 ¬Ω cups of flour</li><li>¬æ teaspoon of salt</li><li>‚Öî cup of cold unsalted butter</li><li>‚Öì cup of cold lard</li><li>‚Öì cup cold water</li></ul>', '<ol><li>Get one 3-quart saucepan</li><li>Mince 1 small onion finely</li><li>Heat saucepan on low heat</li><li>Add 1 ¬Ω pounds of ground lean pork to saucepan</li><li>Mix in 1 minced onion to meat</li><li>Mix in 1 ¬Ω teaspoons of salt.</li><li>Mix in ¬º teaspoon of celery salt</li><li>Mix in ¬º teaspoon of back pepper</li><li>Mix in ¬º teaspoon of sage</li><li>Mix in 1 knife-tip of ground cloves (1/16 teaspoon)</li><li>Chop 1 garlic clove</li><li>Mix in 1 garlic clove</li><li>Cook while stiring slowly on low heat.  Stop when meat has turned brown and half of the liquid has evaporated.</li><li>Cover pan and cook on low heat for 45 minutes.</li><li>While meat is cooking boil a large pot of water.</li><li>Place 3 potatoes in pot and boil until soft.</li><li>Mash potatoes.</li><li>When meat is done cooking, mix in potatoes and allow to cool.</li><li>While cooling, make pie crust.  Begin by filling mixing bowl with 2 ¬Ω cups of flour and ¬æ teaspoon of salt.</li><li>Cube ‚Öî cup of butter</li><li>Cube ‚Öì cup of lard</li><li>Blend in butter and lard to flour mixture</li><li>Drizzle in small amounts of water while mixing until you reach ‚Öì cup.  You may need to mix in an additional 1 teaspoon of water. Mix until the texture is coarse.</li><li>Divide dough into</li><li>Roll dough into disks.</li><li>Cover dough with cling film and allow to cool in the fridge for 30 min.</li><li>Roll one dough out to cover 9-inch pie plate.</li><li>Cover 9-inch pie plate with pie crust doug.</li><li>Add the cooled meat and potatoe mixture.</li><li>Roll one dough out to form the top of the pie.</li><li>Cover pie top.</li><li>Flute and seal the pie.</li><li>Preheat oven to 450 degrees F.</li><li>Cook pie for 10 minutes at 450 degrees F.</li><li>Reduce heat to 350 degrees F.</li><li>Cook for 30-40 min at 350 degrees F.</li></ol>');
INSERT INTO recipes (recipe_name) VALUES ('vanilla slice');
INSERT INTO presentations
    (recipe_id, detail, ingredients, instructions)
VALUES
    (6, 'narrative', '<ul><li>2 sheets ready-rolled puff pastry</li><li>1/2 c (110g) caster sugar</li><li>1/2c (75g) cornflour</li><li>1/4 c (30g) custard powder</li><li>2 1/2c (625ml) milk</li><li>30g butter</li><li>1 egg yolk</li><li>1tsp vanilla extract</li><li>3/4c (180ml) thickened cream</li><li>1 1/2c (240g) icing sugar</li><li>1tsp soft butter</li><li>1/4 c (60ml) passion fruit pulp</li></ul>', '<ol><li>Set the oven to heat to a temperature of 240∞C or 220∞C with a fan assisted oven. Grease a 23cm square, deep edged, cake pan. Then line it with foil taking care to ensure the foil extends a minimum of 10cm over the edges.</li><li>Then grease two other oven trays to place (and bake) the ready-rolled pastry. Bake for approximately fifteen minutes and then cool. Gently flatten the sheets of pastry by hand.</li><li>Once done measure and trim the sheets to fit into the square cake pan. Place one of the sheets in the bottom of the cake pan pressing down gently.</li><li>At this point one mixes together the sugar, cornflour, and custard powder in a saucepan. Slowly add the milk till smooth and combined, without any lumps. Turn on the hob burner to medium high and set the saucepan on top. Add in the butter and stir as it melts and the mixture comes to a boil.</li><li>Continue to stir as it thickens, this usually takes around three minutes to attain a good thick consistency.</li><li>Now take it off the heat and stir in the egg yolk and vanilla extract, making sure to mix well and quickly. You can then cover it with cling film and set aside to cool down to room temperature.</li><li>Taking out a small saucepan and a heatproof bowl, set up a \'water bath\' on the hob by filling the saucepan 3/4 full with water and setting it to simmer (low boil). Pop the bowl on top in a lid-like capacity and then pop in the icing sugar, butter and passion fruit pulp from the icing list. Stir that all together over the simmering water until it melts together into a smooth pourable icing. Set aside.</li><li>In another bowl whip the thickened cream, with a hand mixer, until it forms stif peaks. Then gently fold half of the cream into the custard mixture at a time, using gentle motions and a rounded implement to reduce the number of bubbles in the cream that break.</li><li>Once all the whipped cream has been combined carefully then spread the mixture out over the puff pastry in the cake pan. Take care to make it as smooth as possible as you don\'t want air pockets under the top layer of pastry. When your happy with that, gently place the second measured slice of puff pastry on top, pressing down a bit to \'seat\' the pastry into the custard.</li><li>Pour the icing on top, spreading gently so as not to disturbed what ever flakiness the top pastry has. Cover and cool for a minimum of three hours or overnight.</li></ol>');
INSERT INTO presentations
    (recipe_id, detail, ingredients, instructions)
VALUES
    (6, 'segment', '<ul><li>2 sheets ready-rolled puff pastry</li><li>1/2 c (110g) caster sugar</li><li>1/2c (75g) cornflour</li><li>1/4 c (30g) custard powder</li><li>2 1/2c (625ml) milk</li><li>30g butter</li><li>1 egg yolk</li><li>1tsp vanilla extract</li><li>3/4c (180ml) thickened cream</li><li>1 1/2c (240g) icing sugar</li><li>1tsp soft butter</li><li>1/4 c (60ml) passion fruit pulp</li></ul>', '<ol><li>Preheat oven to 240∞C/220∞C fan-forced. Grease deep, 23cm square cake pan; line with foil, extending the foil 10cm over the sides of pan.</li><li>Place each pastry sheet on separate greased oven trays. Bake about 15 minutes, cool and flatten pastry with hands; place one pastry sheet in pan, trim to fit if necessary.</li><li>Meanwhile, combine sugar, cornflour and custard powder in a medium saucepan; gradually add milk stirring until smooth. Add butter; stir over heat until mixture boils and thickens. Simmer, stirring, about 3minutes or until custard is thick and smooth. Remove from heat; stir in egg yolk and extract. Cover surface with clingfilm; cool to room temperature.</li><li>Make passion fruit icing; place all ingredients into a heatproof bowl over a small saucepan of simmering water; stir until icing is spreadable.</li><li>Whip cream till peaks form. Fold into custard in two batches. Spread custard mixture over pastry in pan. Top with remaining pastry, trim to fit if necessary; press down slightly. Spread pastry with icing; refrigerate 3 hours or overnight.</li></ol>');
INSERT INTO presentations
    (recipe_id, detail, ingredients, instructions)
VALUES
    (6, 'step', '<ul><li>Package of ready-rolled puff pastry</li><li>1/2c (110g) caster sugar</li><li>1/2c (75g) cornflour</li><li>powdered custard mix</li><li>2 1/2c (625ml) milk</li><li>30g butter</li><li>egg</li><li>1tsp vanilla extract</li><li>3/4c (180ml) thickened cream</li><li>1 1/2c (240g) icing sugar</li><li>1tsp soft butter</li><li>1can passion fruit pulp</li></ul>', '<ol><li>Preheat oven to 240∞C/220∞C fan driven.</li><li>Grease a deep 23cm square cake pan.</li><li>Line the pan with foil, extending foil 10cm over sides of pan to act as handles to release the delicate slice once finished.</li><li>Grease two oven trays and set aside.</li><li>Opening your package of puff pastry and removing two sheets put them on the oven trays.</li><li>Once oven is hot bake puff pastry sheets15 minutes then cool.</li><li>Flatten out with your hand.</li><li>Measure and trim both to fit into the cake pan.</li><li>Place one of the two into the bottom of the pan.</li><li>Add 110 grams of sugar to a medium saucepan.</li><li>Add 75 grams cornflour to the saucepan.</li><li>Add powdered custard mix to the saucepan.</li><li>Then gradually add the 625 ml milk, stirring until smooth.</li><li>Set burner to medium high heat</li><li>Add 30g of butter to the saucepan.</li><li>Then stir until mixture boils; simmer while stirring, about 3 minutes or until custard is thick and smooth.</li><li>Remove from heat.</li><li>Separate the yolk from the white of 1 egg, set the white aside, it is unneeded.</li><li>Add the egg yolk to the custard mixture.</li><li>Add 1 tsp of vanilla extract as well.</li><li>Stir the custard mixture well.</li><li>Cover with plastic wrap and set aside to cool to room temperature.</li><li>For the icing, get a small saucepan and a heatproof bowl.</li><li>Fill the saucepan 2/3 the way with water and heat on high till it simmers (low boil).</li><li>Set the heatproof bowl overtop like a lid so it gently warms the bottom.</li><li>Add 240 grams of icing sugar to the heatproof bowl.</li><li>Add 1 tsp of soft butter to the heatproof bowl.</li><li>Add 1 can of passion fruit pulp to the heat proof bowl.</li><li>Mix until the icing is spreadable and pours gently off of your spoon.</li><li>Get out a hand mixer and medium mixing bowl.</li><li>On a medium setting whip the 180 ml thickened cream until it forms stif peaks.</li><li>Using a spatula, fold it into the custard mixture gently, to not break the bubbles. It\'s best to do this in two halves to be more manageable.</li><li>Spread the mixture over the puff pastry in the bottom of the cake pan, smoothing it out.</li><li>Top with the other sheet of the pastry and gently press into the custard.</li><li>Spread the icing over the top smoothly.</li><li>Leave to rest overnight or at least 3 hours.</li></ol>');
